<?phpnamespace MonoGateway;class Order {    protected $order_id = 0;    protected $amount;    protected $ccy = 980;    protected $reference = "";    protected $destination = "";    protected $basket_order = [];    protected $redirect_url;    protected $webhook_url;    protected $customer_emails = [];    public function set_id($order_id) {        $this->order_id = $order_id;    }    public function set_amount($amount) {        $this->amount = $amount;    }    public function set_currency($code) {        $this->ccy = $code;    }    public function set_reference($str) {        $this->reference = $str;    }    public function set_destination($str) {        $this->destination = $str;    }    public function set_basket_order($basket_info) {        $this->basket_order = $basket_info;    }    public function set_redirect_url($url) {        $this->redirect_url = $url;    }    public function set_webhook_url($url) {        $this->webhook_url = $url;    }    public function set_customer_emails($emails) {        $this->customer_emails = $emails;    }    public function get_id(): int {        return $this->order_id;    }    public function get_amount() {        return $this->amount;    }    public function get_currency(): int {        return $this->ccy;    }    public function get_reference(): string {        return $this->reference;    }    public function get_destination(): string {        return $this->destination;    }    public function get_basket_order(): array {        return $this->basket_order;    }    public function get_redirect_url() {        return $this->redirect_url;    }    public function get_webhook_url() {        return $this->webhook_url;    }    public function get_customer_emails() {        return $this->customer_emails;    }}